import React from 'react';
import axios from 'axios';

const API = 'https://api.hgbrasil.com/weather?woeid=55944182&format=json-cors';

export default class App extends React.Component {

  state = {
    city: "Cidade",
    forecast: [],
  }

  componentDidMount() {
    axios.get(API)
      .then(({data}) => {
        this.setState({
          city: data.results.city,
          forecast: data.results.forecast
        });
      });
  }

  render(){
    return (
        <div>
          <h1>{this.state.city}</h1>
          {this.state.forecast.length}
        </div>
    );
  }
}
