import React from 'react';
import axios from 'axios';

const API = 'https://api.hgbrasil.com/weather?woeid=55944182&format=json-cors';

export default class App extends React.Component {

  state = {
    city: "Cidade",
    forecast: [],
  }

  componentDidMount() {
    axios.get(API)
      .then(({data}) => {
        this.setState({
          city: data.results.city,
          forecast: data.results.forecast
        });
      });
  }

  render(){
    return (
        <div>
          <h1>{this.state.city}</h1>
          <table class="striped">
            <thead>
              <tr>
                <th>Data</th>
                <th>Min.</th>
                <th>Max.</th>
              </tr>
            </thead>

            <tbody>
              <tr>
                <td>2020-12-25</td>
                <td>28</td>
                <td>32</td>
              </tr>
            </tbody>
          </table>
        </div>
    );
  }
}
