import React from 'react';

const API = 'https://api.hgbrasil.com/weather?woeid=55944182&format=json-cors';

export default class App extends React.Component {

  state = {
    city: "Cidade",
  }

  componentDidMount() {
    fetch(API)
      .then(response => response.json())
      .then(json => {
        console.log(json);
        this.setState({
          city: json.results.city
        });
      });
  }

  render(){
    return (
        <div>
          <h1>{this.state.city}</h1>
        </div>
    );
  }
}
