import React from 'react';
import axios from 'axios';

const API = 'https://api.hgbrasil.com/weather?woeid=55944182&format=json-cors';

export default class App extends React.Component {

  state = {
    city: "Cidade",
    forecast: [],
  }

  componentDidMount() {
    axios.get(API)
      .then(({data}) => {
        this.setState({
          city: data.results.city,
          forecast: data.results.forecast
        });
      });
  }

  render(){
    return (
        <div className="container">
          <h1>{this.state.city}</h1>
          <table className="striped centered">
            <thead>
              <tr>
                <th>Data</th>
                <th>Min.</th>
                <th>Max.</th>
              </tr>
            </thead>

            <tbody>
              {
                this.state.forecast.map((day, index) => {
                  return (
                      <tr key={index}>
                        <td>{day.date}</td>
                        <td>{day.min}</td>
                        <td>{day.max}</td>
                      </tr>
                    );
                })
              }
            </tbody>

          </table>
        </div>
    );
  }
}
