import React from 'react';
import axios from 'axios';

const API = 'https://api.hgbrasil.com/weather?woeid=55944182&format=json-cors';

export default class App extends React.Component {

  state = {
    city: "Cidade",
    forecast: [],
  }

  componentDidMount() {
    axios.get(API)
      .then(({data}) => {
        this.setState({
          city: data.results.city,
          forecast: data.results.forecast
        });
      });
  }

  render(){
    return (
        <div>
          <h1>{this.state.city}</h1>
          <table class="highlight">
            <thead>
              <td>Data</td>
              <td>Min.</td>
              <td>Max.</td>
            </thead>

            <tbody>
              <td>2020-12-25</td>
              <td>15</td>
              <td>29</td>
            </tbody>
          </table>
        </div>
    );
  }
}
